const DnsProve = require('../lib/dnsprover');
let target = process.argv[process.argv.length -1]
console.log(process.argv[process.argv.length -1]);

const main = async () => {
    const dnsprove = new DnsProve();
    const dnsResult = await dnsprove.lookup('TXT', target);
    console.log({dnsResult})
}

main()